import { Request, Response } from 'express';
import UserController from '../controllers/UserController';
import bcrypt from 'bcrypt';
import jwt from 'jwt-simple';
// import httpStatus from 'http-status';
// import _ from 'lodash';
// import Handler from '../responses/Handler';

class TokenRoutes {
	auth(req: Request, res: Response) {
		const credentials = {
			email: req.body.email,
			password: req.body.password
		};
		if (credentials.hasOwnProperty('email') && credentials.hasOwnProperty('password')) {
			UserController.getAuthEmail(credentials.email)
				.then(user => {
					const password = bcrypt.compareSync(credentials.password, user.password);
					if (password) {
						return res.json({
							token: jwt.encode({ id: user.id }, process.env.SECRET)
						});
					} else {
						return res.json({ msg: 'senha não confere' });
					}
				})
				.catch(erro => res.json({ msg: 'Usuário não existe' }));
			// .catch(erro => res.sendStatus(httpStatus.UNAUTHORIZED));
			// .then(_.partial(Handler.AuthSuccess, res, credentials))
			// .catch(_.partial(Handler.AuthFail, req, res));
		}
	}
}

export default new TokenRoutes();

/** @format */

import passport from 'passport';
import { ExtractJwt, JwtFromRequestFunction, Strategy } from 'passport-jwt';
import UserController from '../controllers/UserController';

class Auth {
	// public opts: { secretOrKey: string; jwtFromRequest: JwtFromRequestFunction };
	public usuario: number;

	constructor() {
		this.usuario = 0;
	}

	config() {
		const opts = {
			secretOrKey: process.env.SECRET,
			// jwtFromRequest: ExtractJwt.fromAuthHeaderWithScheme('jwt')
			jwtFromRequest: ExtractJwt.fromAuthHeaderAsBearerToken()
		};

		const strategy = new Strategy(opts, (jwtPayload, done) => {
			UserController.getAuthId(jwtPayload.id)
				.then(user => {
					if (user) {
						this.usuario = user.id; //para a funcao serializeUser
						return done(null, { id: user.id, email: user.email });
					}
					return done(null, false);
				})
				.catch(error => {
					return done(error, null);
				});
		});

		passport.use(strategy);

		passport.serializeUser((user, done) => done(null, this.usuario));

		passport.deserializeUser((id, done) => {
			UserController.getAuthId(this.usuario).then(user =>
				done(null, { id: user.id, email: user.email })
			);
		});

		return {
			initialize: () => passport.initialize(),
			authenticate: () => passport.authenticate('jwt', { session: true }),
			session: () => passport.session()
		};
	}
}

export default new Auth();
